import cors from 'cors';
import dotenv from 'dotenv';
import express from 'express';
import mongoose from 'mongoose';
import swaggerUi from 'swagger-ui-express';

import allRoutes from './routes';
import apiDocumentation from '../swagger.json';

dotenv.config();
const app = express();
app.use(express.json());
app.use(cors());

mongoose.connect(process.env.DB, { useNewUrlParser: true, useUnifiedTopology: true, useCreateIndex: true });
mongoose.connection.on('error', (err) => { console.log(`Error at mongo: ${err}`); });
mongoose.connection.on('connected', () => { console.log('Connected to mongoDB'); });

app.use('/api', allRoutes);
app.use(`/api/documentation`, swaggerUi.serve, swaggerUi.setup(apiDocumentation));
app.get('**', (req, res) => res.status(200).json({ status: 200, message: `Welcome to Neotech Solutions Backend` }));

app.listen(process.env.PORT, () => { console.log('Server Started on', process.env.PORT); });

export default app;

import Router from 'express';
import employeesRouter from './employeesRoute';

const router = Router();
router.use('/employees', employeesRouter);

export default router;

import Router from 'express';
import employees from '../controllers/employeesController';
import { validateCreateEmployee } from '../middlewares/schemaMiddleware';

const employeesRouter = Router();

employeesRouter
  .post('/create-employee', validateCreateEmployee, employees.createEmployee);

export default employeesRouter;

import { INTERNAL_SERVER_ERROR, CREATED } from 'http-status';
import employeesHelper from '../helpers/employeesHelper';
import responseHelper from '../services/responseHelper';

/**
* This class contains all methods (functions) required to handle
* createEmployees function.
* fetchEmployees function.
* Etc ...
*/
class EmployeesController {
  /**
     * Handle createEmployees.
     * @param {object} req user request.
     * @param {object} res data response.
     * @returns {object} returned object of data.
     */
  static async createEmployee(req, res) {
    try {
      const data = await employeesHelper.saveData(req.body);

      responseHelper.handleSuccess(CREATED, 'Employee created successfully.', data);
      return responseHelper.response(res);
    } catch (error) {
      return res.status(INTERNAL_SERVER_ERROR).json({
        status: INTERNAL_SERVER_ERROR,
        message: error.toString()
      });
    }
  }
}

export default EmployeesController;

import EmployeeSchema from '../models/employeesModel';

/**
* This class contains all methods (functions) required to handle
* saveData function.
* Etc ...
*/
class UserHelper {
  /**
   * Save data into employees table in database.
   * @param {object} body employee details.
   * @returns {object} saved data details.
   */
  static async saveData(body) {
    const data = await EmployeeSchema.create({
      name: body.name,
      email: body.email,
      phone: body.phone,
      employmentDate: body.employmentDate,
      birthDate: body.birthDate,
      home: body.home
    });
    return data;
  }
}

export default UserHelper;

import mongoose from 'mongoose';

const Schema = mongoose.Schema;
const EmployeesSchema = new Schema({
  banned: { type: Boolean, default: false },
  name: { type: String, required: [true, 'Name is required'] },
  email: { type: String, required: [true, 'Email is required'] },
  phone: { type: String, required: [true, 'Phone is required'] },
  employmentDate: { type: String, required: [true, 'Date of employment is required'] },
  birthDate: { type: String, required: [true, 'Date of Birth is required'] },
  home: [{
    city: { type: String, required: [true, 'City is required'] },
    ZIPCode: { type: String, required: [true, 'ZIP Code is required'] },
    AddressLine1: { type: String, required: [true, 'AddressLine1 is required'] },
    AddressLine2: { type: String, required: [true, 'AddressLine2 is required'] }
  }],
  createdAt: { type: Date, default: Date.now },
  updatedAt: { type: Date, default: Date.now },
});

export default mongoose.model('Employees', EmployeesSchema);

/**
* This class contains all methods (functions) required to handle
* handleSuccess function.
* handleError function.
* response function.
*/
class ResponseHelpers {
  /**
   * Handle Success Response.
   * @param {integer} statusCode status code.
   * @param {integer} message message.
   * @param {object} data data.
   * @returns {null} .
   */
  static handleSuccess(statusCode, message, data) {
    this.statusCode = statusCode;
    this.message = message;
    this.data = data;
    this.type = 'success';
  }

  /**
   * Handle Error Response.
   * @param {integer} statusCode status code.
   * @param {integer} message message.
   * @returns {null} .
   */
  static handleError(statusCode, message) {
    this.statusCode = statusCode;
    this.message = message;
    this.type = 'error';
  }

  /**
   * Handle response output.
   * @param {object} res response.
   * @returns {object} response.
   */
  static response(res) {
    if (this.type === 'success') {
      return res.status(this.statusCode).json({
        status: this.statusCode,
        message: this.message,
        data: this.data,
      });
    }
    return res.status(this.statusCode).json({
      status: this.statusCode,
      message: this.message,
    });
  }
}

export default ResponseHelpers;

/* eslint-disable import/prefer-default-export */

import Joi from 'joi';
import joiPhone from 'joi-phone-number';
import validateSchema from './validateSchema';

const customJoi = Joi.extend(joiPhone);

/**
* This method handle validation create employee details.
* @param {object} req an employee details request.
* @param {object} res a response.
* @param {object} next if true move response.
* @returns {string}  a status and data.
*/
const validateCreateEmployee = (req, res, next) => {
  const dataSchema = Joi.object()
    .keys({
      name: Joi.string().min(3).max(50).required()
        .messages({
          'any.required': 'name is required',
          'string.empty': 'name is not allowed to be empty',
        }),
      email: Joi.string().email().required().messages({
        'any.required': 'email is required',
        'string.empty': 'email is not allowed to be empty',
        'string.email': 'email must be a valid email',
      }),
      phone: customJoi.string().phoneNumber({ format: 'international', strict: true }).required().messages({
        'any.required': 'phone is required',
        'string.empty': 'phone is not allowed to be empty',
        'phoneNumber.invalid': 'phone did not seem to be a phone number',
      }),
      employmentDate: Joi.date().required()
        .messages({
          'any.required': 'employmentDate is required',
          'string.empty': 'employmentDate is not allowed to be empty',
        }),
      birthDate: Joi.date().min(new Date('1900-01-01').toISOString().split('T')[0]).max(new Date().toISOString().split('T')[0]).required()
        .messages({
          'any.required': 'birthDate is required',
          'string.empty': 'birthDate is not allowed to be empty',
        }),
      home: Joi.array().required().items(
        Joi.object({
          city: Joi.string().required().messages({
            'any.required': 'city is required',
            'string.empty': 'city is not allowed to be empty',
          }),
          ZIPCode: Joi.string().required().messages({
            'any.required': 'ZIPCode is required',
            'string.empty': 'ZIPCode is not allowed to be empty',
          }),
          AddressLine1: Joi.string().required().messages({
            'any.required': 'AddressLine1 is required',
            'string.empty': 'AddressLine1 is not allowed to be empty',
          }),
          AddressLine2: Joi.string().required().messages({
            'any.required': 'AddressLine2 is required',
            'string.empty': 'AddressLine2 is not allowed to be empty',
          }),
        }).required()
      )
    })
    .options({ abortEarly: false });

  return validateSchema(dataSchema, req.body, res, next);
};

export {
  validateCreateEmployee,
};

import { BAD_REQUEST } from 'http-status';
import helper from '../services/responseHelper';

const validateSchema = (schema, body, res, next) => {
  const { error } = schema.validate(body);

  if (error) {
    const errors = error.details.map((err) => err.message);
    helper.handleError(BAD_REQUEST, errors[0].replace(/"/g, ''));
    return helper.response(res);
  }

  return next();
};

export default validateSchema;
